PRAGMA foreign_keys=ON;
BEGIN TRANSACTION;
CREATE TABLE IMPOSTAZIONI (NOME TEXT PRIMARY KEY, VALORE TEXT NOT NULL );
CREATE TABLE RICERCA (QUERY TEXT, GEO BIT, TIPO INTEGER DEFAULT 0, DATA_INIZIO TIMESTAMP, DATA_FINE TIMESTAMP, TIME_STAMP TIMESTAMP DEFAULT CURRENT_TIMESTAMP PRIMARY KEY );
CREATE TABLE TWEET (TWEETID INTEGER PRIMARY KEY, USERNAME TEXT NOT NULL, USERPROPIC TEXT NOT NULL, TESTO TEXT NOT NULL, IMMAGINE BIT NOT NULL, GEOAVGLATITUDE REAL NOT NULL, GEOAVGLONGITUDE REAL NOT NULL, POSTO TEXT NOT NULL, LINGUA TEXT NOT NULL, TIME_STAMP TIMESTAMP NOT NULL );
CREATE TABLE RELRICERCATWEET (TIME_STAMP TIMESTAMP, TWEETID INTEGER, CONSTRAINT timestamp_fk FOREIGN KEY (TIME_STAMP) REFERENCES RICERCA(TIME_STAMP) ON DELETE CASCADE, CONSTRAINT tweetid_fk FOREIGN KEY (TWEETID) REFERENCES TWEET(TWEETID) ON DELETE CASCADE );
COMMIT;
